<?php get_header();global $post;$category = get_the_category($post->ID);$relatedCategory = $category[0]->cat_ID;?>    <!-- CONTENT --><div class="row content">    <div class="ten columns leftcontent"><?php if (have_posts()) : while (have_posts()) :    the_post(); ?>    <?php $postaffurl = get_post_meta($post->ID, 'affiliate_url', true); ?>    <?php $image = wp_get_attachment_image_src(get_post_thumbnail_id($post->ID), 'single-thumb'); ?>    <?php $rating = get_post_meta($post->ID, 'affiliate_rating', true); ?>    <?php    $showmeta_single = ready_review('showmeta_single');    ?>    <h1 <?php if (!isset($showmeta_single) || (isset($showmeta_single) && $showmeta_single == 'showmeta_single_hide')) { ?> class="postmargin"<?php } else { ?>class="single"<?php } ?>><?php the_title(); ?></h1>    <?php if (isset($showmeta_single) && $showmeta_single == 'showmeta_single_show') { ?>        <div class="post_meta_single">            <ul>                <li class="metacats"><?php the_category(' '); ?></li>                <li>//</li>                <li><?php the_time('F d, Y'); ?></li>                <li>//</li>                <li><?php comments_popup_link(__('Comments', 'ready_review'), __('1 Comment', 'ready_review'), __('% Comments', 'ready_review')); ?></li>                <li>//</li>                <li><?php the_tags(); ?></li>            </ul>        </div>    <?php } ?>    <!-- post -->    <?php    $similar_posts = ready_review('similar_posts');    if (isset($similar_posts) && $similar_posts == 'similar_posts_show') {    ?>    <div <?php post_class('noborder'); ?>>    <?php    }    else {    ?>    <div <?php post_class(); ?>>        <?php } ?>        <p>            <?php if ($image) { ?>                <a href="<?php echo $postaffurl; ?>" rel="nofollow"><img src="<?php echo $image[0]; ?>"                                                                         alt="<?php the_title(); ?>" align="left"                                                                         class="affiliate-image"></a>            <?php } ?>            <?php the_content();            wp_link_pages(array('before' => '<div class="page-link">' . __('Pages:', 'ready_review'), 'after' => '</div>'));            ?>        <div class="clear"></div>        <?php if ($postaffurl) { ?>            <div class="single-meta">                <?php if ($rating) { ?>                    <div class="stars"><img                                src="<?php echo get_template_directory_uri(); ?>/images/<?php echo $rating; ?>-star.png"                                border="0"/></div>                <?php } ?>                <div class="buybutton"><a href="<?php echo $postaffurl; ?>" rel="nofollow" class="button"                                          style="color:#fff;"><?php _e('Buy Now', 'ready_review'); ?> &raquo;</a></div>                <div class="clear"></div>            </div>        <?php } ?>        </p>    </div>    <!-- end post -->    <!-- Similar Posts -->    <?php if (isset($similar_posts) && $similar_posts == 'similar_posts_show') { ?>        <?php require_once dirname(__FILE__) . '/custom/similar.php'; ?>    <?php } ?>    <?php comments_template(); // Get wp-comments.php template ?><?php endwhile;endif;?>    </div><!--end of .ten columns (left content) --><?php get_footer(); ?>